package pages

func fileBrowserTemplate() string {
	var template string
	template = `
	<html lang="en">
	<head>
		<meta charset="UTF-8">
		<meta name="viewport" content="width=device-width, initial-scale=1.0">
		<title>File Browser</title>
	</head>
	<body>
		<div>
			<h1>Index of {{.Path}}</h1>
		</div>
		<div>
			<a href="file://{{.Parent}}" style="font-size: 16px;">../</a>
		</div>
		{{range .Dirs}}
			<div>
				<img src="data:image/png;base64,">
				<a href="file://{{$.Path}}{{if $.NotRootDir}}/{{end}}{{.}}" style="font-size: 16px;">{{.}}/</a>
			</div>
		{{end}}
		{{range .Files}}
			<div>
				<img src="data:image/png;base64,">
				<span style="font-size: 16px;">{{.}}</span>
			</div>
		{{end}}
	</html>
	`
	return template
}
